import Modal from 'react-modal';
import { useForm } from 'react-hook-form'
import {FiX} from 'react-icons/fi'
import { Container, Error } from './style';
import api from '../../services/api';

interface NewActivyModalProps{
    isOpen: boolean;
    onRequestClose: () => void;

}

interface NewActivyModalData{
    courseunit: string;
    activy: string;
    date: Date;
}

export function NewActivyModal({isOpen, onRequestClose}:NewActivyModalProps){


    const {register, handleSubmit, formState: {errors}} = useForm<NewActivyModalData>()
    const onSubmit = handleSubmit(data => api.post('/activy', data).then(response => alert(response.data)))

    return(

        <Modal isOpen={isOpen}
               onRequestClose={onRequestClose}
               overlayClassName="react-modal-overlay"
               className="react-modal-content"
        >
            <Container>
                <h2>Cadastrar Atividade</h2>
                <button type="button"
                        onClick={onRequestClose}
                        className="react-modal-close"
                >
                    <FiX size={20}/>
                </button>
                <form onSubmit={onSubmit}>
                    <input type="text" 
                           placeholder = "Unidade Curricular"
                           {...register("courseunit", {required:true})}
                    />
                    {errors.courseunit && <Error>O preechimento do campo é obrigatório</Error>}
                    <input type="text" 
                           placeholder = "Atividade"
                           {...register("activy", {required:true})}
                    />
                    {errors.activy && <Error>O preechimento do campo é obrigatório</Error>}
                    <input type="date" 
                           placeholder = "Data da Atividade"
                           {...register("date", {required:true})}
                    />
                    {errors.date && <Error>O preechimento do campo é obrigatório</Error>}
                    <button type="submit">
                        Cadastrar
                    </button>
                </form>

            </Container>

        </Modal>
    )
}
